const myModule=(()=>{"use strict";let e=document.getElementById("result"),t=document.querySelector("#btnPerimSquare"),l=document.querySelector("#btnAreaSquare"),r=document.querySelector("#btnPerimTriangle"),n=document.querySelector("#btnAreaTriangle"),i=document.querySelector("#btnDiameCircle"),d=document.querySelector("#btnPerimCircle"),a=document.querySelector("#btnAreaCircle"),c=e=>4*e,u=e=>e*e,m=(e,t,l)=>e+t+l,E=(e,t)=>e*t/2,o=e=>2*e,s=e=>6.2832*e,y=e=>e*e*3.1416;t.addEventListener("click",()=>{let t=Number(document.getElementById("inputSide").value),l=`El perimetro del cuadrado es: ${c(t)}cm`;e.innerText=l}),l.addEventListener("click",()=>{let t=Number(document.getElementById("inputSide").value),l=`El \xc1rea del cuadrado es: ${u(t)}cm2`;e.innerText=l}),r.addEventListener("click",()=>{let t=Number(document.getElementById("side1").value),l=Number(document.getElementById("side2").value),r=Number(document.getElementById("side3").value),n=`El perimetro del triangulo es: ${m(t,l,r)}cm`;e.innerText=n}),n.addEventListener("click",()=>{let t=Number(document.getElementById("base").value),l=Number(document.getElementById("altura").value),r=`El \xc1rea del triangulo es: ${E(t,l)}cm2`;e.innerText=r}),i.addEventListener("click",()=>{let t=Number(document.getElementById("radio").value),l=`El diametro del circulo es ${o(t)}cm`;e.innerText=l}),d.addEventListener("click",()=>{let t=Number(document.getElementById("radio").value),l=`El perimetro del circulo es ${s(t)}cm`;e.innerText=l}),a.addEventListener("click",()=>{let t=Number(document.getElementById("radio").value),l=`El \xc1rea del circulo es ${y(t)}cm2`;e.innerText=l})})();